package com.pedro.festafimdeano.views;import androidx.appcompat.app.AppCompatActivity;import android.os.Bundle;import android.view.View;import android.widget.CheckBox;import com.pedro.festafimdeano.data.SecurityPreferences;import com.pedro.festafimdeano.R;import constant.FimDeAnoConstants;public class DetailsActivity extends AppCompatActivity implements View.OnClickListener {    private ViewHolder mViewHolder = new ViewHolder();    private SecurityPreferences SecurityPreferences;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_details);        this.SecurityPreferences = new SecurityPreferences(this);        this.mViewHolder.checkParticipate = findViewById(R.id.check_participate);        this.mViewHolder.checkParticipate.setOnClickListener(this);    }    @Override    public void onClick(View v) {        if(v.getId() == R.id.check_participate){            if(this.mViewHolder.checkParticipate.isChecked()){                this.SecurityPreferences.storeString(FimDeAnoConstants.PRESENCE_KEY,FimDeAnoConstants.CONFIRMATION_YES);            }else{                this.SecurityPreferences.storeString(FimDeAnoConstants.PRESENCE_KEY,FimDeAnoConstants.CONFIRMATION_NO);            }        }    }    private static class ViewHolder{        CheckBox checkParticipate;    }}